import styled from "@emotion/styled";
import { Box } from "@mui/system";

export const Container = styled(Box)(({ theme }) => ({
  width: "100%",
  display: "flex",
  gap: ".5rem",
  padding: "1rem 0",
}));

export const ProfileWrapper = styled(Box)(({ theme }) => ({
  flex: "1",
  height: "10rem",
  display: "grid",
  placeItems: "center",
}));

export const ProfileDetails = styled(Box)(({ theme }) => ({
  flex: "3",
  display: "flex",
  flexDirection: "column",
  gap: "2rem",
}));

export const UserName = styled(Box)(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  gap: "1rem",
  "& p": {
    fontSize: "1.2rem",
    fontWeight: "500",
    letterSpacing: ".5px",
  },
}));

export const Img = styled("img")(({ theme }) => ({
  width: "140px",
  height: "140px",
  borderRadius: "50%",
  objectFit: "cover",
}));

export const Bio = styled(Box)(({ theme }) => ({
  display: "flex",
  flexDirection: "column",
  gap: ".3rem",
  "& p:nth-of-type(1)": {
    fontWeight: "500",
  },
}));

export const Button = styled("button")(({ theme }) => ({
  padding: "5px 6px",
  border: "1px solid #d1d1d1",
  backgroundColor: "transparent",
  borderRadius: "4px",
  fontWeight: "600",
  fontSize: ".7rem",
  cursor: "pointer",
}));

export const EditWrapper = styled(Box)(({ theme }) => ({
  display: "flex",
  flexDirection: "column",
  gap: ".5rem",
}));

export const Input = styled("input")(({ theme }) => ({
    height: "20px",
    width: "80%"
}));
