import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setPost } from "../../features/postSlice";
import { Container, Post, PostBtn, Posts } from "./Body.styled";

const Body = () => {
    const [open, setOpen] = useState()
  const [postImg, setPostImg] = useState(null);
  const posts = useSelector((store) => store.post);
  const dispatch = useDispatch();
  const postHandler = (e) => {
    e.preventDefault();
    if (e.target.files && e.target.files[0]) {
      setPostImg(URL.createObjectURL(e.target.files[0]));
    }
  };
  return (
    <Container>
      <PostBtn
        htmlFor="post"
        onClick={() =>
          dispatch(setPost({ id: Math.random() * 10000, postUrl: postImg }))
        }
      >
        New post
      </PostBtn>
      <input
        style={{ display: "none" }}
        type="file"
        id="post"
        onChange={postHandler}
      />
      <Posts>
        {posts.map((post) => {
            return <Post key={post.id} src={post.PostImg} alt="post" />;
        })}
      </Posts>
    </Container>
  );
};

export default Body;
