import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Bio,
  Button,
  Container,
  EditWrapper,
  Img,
  Input,
  ProfileDetails,
  ProfileWrapper,
  TextArea,
  UserName,
} from "./Header.styled";
import { edit as editUser } from "../../features/profileSlice";

const Header = () => {
  const [edit, setEdit] = useState(false);
  const [userName, setUserName] = useState("");
  const [name, setName] = useState("");
  const [bio, setBio] = useState("");

  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();

  const editHandler = () => {
    dispatch(editUser({ userName: userName }));
    setEdit(false);
  };

  return (
    <Container>
      <ProfileWrapper>
        <Img
          src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQ--rJ7Xa-1PtEpg5FjvgqjKda1FF9lUKrvg&usqp=CAU"
          alt="profile"
        />
      </ProfileWrapper>
      <ProfileDetails>
        {!edit ? (
          <>
            <UserName>
              <p>{user.userName}</p>
              <Button onClick={() => setEdit(true)}>Edit Profile</Button>
            </UserName>
            <Bio>
              <p>{user.name}</p>
              <p>{user.bio}</p>
            </Bio>
          </>
        ) : (
          <EditWrapper>
            <Input
              type="text"
              placeholder="Username"
              onChange={(e) => {
                setUserName(e.target.value);
              }}
            />
            <Input
              type="text"
              placeholder="Profile name"
              onChange={(e) => {
                setName(e.target.value);
              }}
            />
            <TextArea
              type="text"
              placeholder="Bio"
              onChange={(e) => {
                setBio(e.target.value);
              }}
            ></TextArea>
            <Button width="3rem" onClick={editHandler}>
              Save
            </Button>
          </EditWrapper>
        )}
      </ProfileDetails>
    </Container>
  );
};

export default Header;
