import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { post as getPost } from "../../features/postSlice";
import { Container, Post, PostBtn, Posts } from "./Body.styled";

const Body = () => {
  const [post, setPost] = useState(null);
  const posts = useSelector((store) => store.post);
  const dispatch = useDispatch();
  const postHandler = (e) => {
    if (e.target.files && e.target.files[0]) {
      setPost(URL.createObjectURL(e.target.files[0]));
    }
  };
  return (
    <Container>
      <PostBtn
        htmlFor="post"
        onClick={() => dispatch(getPost({ id: 2, PostImg: post }))}
      >
        New post
      </PostBtn>
      <input
        style={{ display: "none" }}
        type="file"
        id="post"
        onChange={postHandler}
      />
      <Posts>
        {posts.map((post) => {
          return (
            <Post
              src="https://i.pinimg.com/736x/80/0e/9a/800e9a0d7eb0336ca302b3e4c78ad6e9.jpg"
              alt="post"
            />
          );
        })}
      </Posts>
    </Container>
  );
};

export default Body;
