import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  userName: "_the_.santhosh",
  name: "Santhsoh Prabhakaran",
  bio: "• ʟɪꜰᴇ ɪꜱ ɴᴏᴛ ᴀ ᴘʀᴏʙʟᴇᴍ ᴛᴏ ʙᴇ ꜱᴏʟᴠᴇᴅ ʙᴜᴛ ᴀ ʀᴇᴀʟɪᴛʏ ᴛᴏ ʙᴇ ᴇxᴘᴇʀɪᴇɴᴄᴇᴅ 🌼🍂",
};

const profileSlice = createSlice({
  name: "profile",
  initialState,
  reducers: {
    edit: (state, action) => {
      state = action.payload
    },
  },
});

export const { edit } = profileSlice.actions;
export default profileSlice.reducer;
