import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Bio,
  Button,
  ButtonWrapper,
  Container,
  EditWrapper,
  Img,
  Input,
  Label,
  ProfileDetails,
  ProfileWrapper,
  TextArea,
  UserName,
} from "./Header.styled";
import { editUser } from "../../features/profileSlice";

const Header = () => {
  const [edit, setEdit] = useState(false);
  const [userName, setUserName] = useState("");
  const [name, setName] = useState("");
  const [bio, setBio] = useState("");
  const [image, setImage] = useState(null);

  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();

  const onImageChange = (e) => {
    if(e.target.files && e.target.files[0]){
        setImage(URL.createObjectURL)
    }
  }

  const editHandler = () => {
    dispatch(editUser({ userName, bio, name, profileImg: image }));
    setEdit(false);
  };

  return (
    <Container>
      <ProfileWrapper>
        <Img src={user.profileImg} alt="profile" />
      </ProfileWrapper>
      <ProfileDetails>
        {!edit ? (
          <>
            <UserName>
              <p>{user.userName}</p>
              <Button onClick={() => setEdit(true)}>Edit Profile</Button>
            </UserName>
            <Bio>
              <p>{user.name}</p>
              <p>{user.bio}</p>
            </Bio>
          </>
        ) : (
          <EditWrapper>
            <Input
              type="text"
              placeholder="New username"
              onChange={(e) => {
                setUserName(e.target.value);
              }}
            />
            <Input
              type="text"
              placeholder="New profile name"
              onChange={(e) => {
                setName(e.target.value);
              }}
            />
            <TextArea
              type="text"
              placeholder="New bio"
              onChange={(e) => {
                setBio(e.target.value);
              }}
            ></TextArea>
            <ButtonWrapper>
              <Button width="3rem" onClick={editHandler}>
                Save
              </Button>
              <Label htmlFor="inputTag">Change profile picture</Label>
              <input
                style={{
                  display: "none",
                }}
                id="inputTag"
                type="file"
                onChange={onImageChange}
              ></input>
            </ButtonWrapper>
          </EditWrapper>
        )}
      </ProfileDetails>
    </Container>
  );
};

export default Header;
